import { Component } from "react";
// import { v4 } from "uuid";
import { PRODUCT } from "./Api";
// import Basket from "../images/Empty-Cart.svg";
import { client } from "../index";
import Notiflix from "notiflix";

export default class HomePage extends Component {
  state = {
    showBasker: false,
    productAll: [],
  };

  async componentDidMount() {
    try {
      const result = await client.query({
        query: PRODUCT,
      });
      const qwe = result.data.category.products.map((data) => {
        const obj = {
          product: data.gallery[0],
          id: data.id,
        };
        return obj;
      });
      this.setState({ productAll: qwe });
    } catch (error) {
      console.log(error.message);
      Notiflix.Notify.failure(`${error.message}`);
    }
  }

  // id = (e) => {
  //   console.log(e.curr.id);
  // };

  // basket = (e) => {
  //   console.log(e.currentTarget);
  //   this.setState({ showBasket: !this.state.showBasket });
  // };

  render() {
    const { productAll } = this.state;
    return (
      <main className="container">
        <p className="category-name">Category name</p>
        <ul className="gallery_list">
          {productAll.length > 0 &&
            productAll.map(({ product, id }) => {
              return (
                <li id={id} className="gallery_item" key={id}>
                  <img
                    width="354"
                    height="330"
                    src={product}
                    alt="our products"
                  />
                </li>
              );
            })}
        </ul>
      </main>
    );
  }
}
