import { Component } from "react";

export default class ModalBag extends Component {
  state = {
    items: null,
  };

  componentDidMount() {
    const itemStorage = JSON.parse(localStorage.getItem("productItems"));
    if (itemStorage) {
      const item = itemStorage.map(
        ({ name, brand, gallery, id, attributes, description }) => {
          return {
            name: name,
            brand,
            gallery: gallery[0],
            id,
            attributes,
            description,
          };
        }
      );
      return this.setState({ items: item });
    }
  }

  render() {
    const { items } = this.state;
    return (
      <div className="modal_container-bag">
        {items ? (
          <div>
            <p>
              <span>My bag,</span>
              {` ${items.length} items`}
            </p>
            <ul>
              {items.map((data) => {
                console.log(data);
              })}
            </ul>
          </div>
        ) : (
          <div>Корзина пуста</div>
        )}
      </div>
    );
  }
}
