import { Component } from "react";
import { gql } from "@apollo/client";
import { Query } from "@apollo/client/react/components";

const CURRENCIES = gql`
  query {
    currencies {
      label
      symbol
    }
  }
`;

export default class ChoiceCurrency extends Component {
  choise = (e) => {
    const { textContent } = e.target;
    console.log(e.currentTarget);
    // const qwe = textContent.split().join(",");
    // console.log(qwe[0]);
  };

  render() {
    return (
      <ul onClick={this.choise} className="currency_list">
        <Query query={CURRENCIES}>
          {({ loading, data }) => {
            if (loading) return <div>Loading. . .</div>;
            if (data) {
              const currenciesItems = data.currencies.map(
                ({ symbol, label }) => {
                  return (
                    <li key={symbol} className="currency_item">
                      <span>{symbol}</span> {label}
                    </li>
                  );
                }
              );
              return currenciesItems;
            }
          }}
        </Query>
      </ul>
    );
  }
}
