import { Component } from "react";
import { gql } from "@apollo/client";
import { Query } from "@apollo/client/react/components";

const ITEMS = gql`
  query {
    category {
      products {
        category
      }
    }
  }
`;

export default class HomePage extends Component {
  render() {
    return (
      <main>
        <h1>Category name</h1>
        <ul>
          <Query query={ITEMS}>
            {({ loading, data, error }) => {
              if (data) {
                const { products } = data.category;
                const arr = [];
                for (let cat of products) {
                  if (!arr.includes(cat.category)) {
                    console.log(arr.push(cat.category));
                    return arr.push(cat.category);
                  }
                }

                // console.log(products);
                // const qwe = products.map(({ category }) => {
                //   return <li key={category}>{category}</li>;
                // });
                // return qwe;
              }
            }}
          </Query>
        </ul>
      </main>
    );
  }
}
