// import { Route, Routes } from "react-router-dom";
import { Component } from "react";
import Header from "./components/header";
import HomePage from "./components/homePage";
import ItemPage from "./components/ItemPage";

class App extends Component {
  state = {
    symbol: "",
    modalBag: false,
    itemsBag: null,
    pageItem: false,
  };

  componentDidMount() {
    const symbol = localStorage.getItem("currencySymbol");
    this.setState({ symbol: symbol });
  }

  changeSymbol = (symbol) => {
    this.setState({ symbol: symbol });
  };

  toggleModalBasket = () => {
    this.setState({ modalBag: !this.state.modalBag });
  };

  addCounterBag = (arrItem) => {
    this.setState({ itemsBag: arrItem.length });
  };

  changePage = (e) => {
    console.log(e);
    this.setState({ changePage: !this.state.changePage });
  };

  render() {
    const { symbol, modalBag, itemsBag, pageItem } = this.state;
    return (
      <div className="App">
        <Header
          changeSymbol={this.changeSymbol}
          modalBag={this.toggleModalBasket}
          counter={itemsBag}
        />
        {!pageItem && (
          <HomePage
            symbolCard={symbol}
            modalBag={modalBag}
            toggle={this.toggleModalBasket}
            itemBag={(i) => this.addCounterBag(i)}
            changePage={(e) => this.changePage(e)}
          />
        )}
        {pageItem && <ItemPage />}
      </div>
    );
  }
}

export default App;
