import { Component } from "react";
import { v4 } from "uuid";
import { gql } from "@apollo/client";
import { Query } from "@apollo/client/react/components";
// import Basket from "../images/Empty-Cart.svg";
import { client } from "../index";
import Notiflix from "notiflix";

export default class HomePage extends Component {
  state = {
    showBasker: false,
    product: null,
  };

  async componentDidMount(prev, state) {
    try {
      const qwe = await client.query({
        query: gql`
          query {
            category {
              products {
                gallery
                brand
                name
                prices {
                  amount
                  currency {
                    symbol
                  }
                }
              }
            }
          }
        `,
      });
      this.setState({ product: qwe.data.category.products });
      console.log(this.state);
    } catch (error) {
      Notiflix.Notify.failure(`${error.message}`);
    }
  }

  // basket = (e) => {
  //   console.log(e.currentTarget);
  //   this.setState({ showBasket: !this.state.showBasket });
  // };

  render() {
    // const { showBasket } = this.state;
    console.log(this.state);
    return (
      <main className="container">
        <p className="category-name">Category name</p>
        <ul className="gallery_list">
          <Query query={ITEMS}>
            {({ loading, data, error }) => {
              if (data) {
                const { products } = data.category;
                const gallery = products.map((data) => {
                  return (
                    <li className="gallery_item" key={v4()}>
                      <img
                        src={data.gallery[0]}
                        width="354"
                        height="444"
                        alt="clothes"
                      />
                      <p className="gallery_brand">
                        {data.brand} {data.name}
                      </p>
                      <p className="gallery_price">
                        <span>{data.prices[0].currency.symbol}</span>
                        {data.prices[0].amount}
                      </p>
                    </li>
                  );
                });
                return gallery;
              }
            }}
          </Query>
        </ul>
      </main>
    );
  }
}

const ITEMS = gql`
  query {
    category {
      products {
        gallery
        brand
        name
        prices {
          amount
          currency {
            symbol
          }
        }
      }
    }
  }
`;

