import { Component } from "react";
import { v4 } from "uuid";
import Api from "./Api";
const fetchProduct = new Api();

export default class ItemPage extends Component {
  state = {
    item: null,
    price: 0,
    currentImage: null,
    active: null,
    activeId: null,
    activeAtribute: null,
  };

  async componentDidMount() {
    const fetchItem = await fetchProduct.getProductId(this.props.itemId);
    const item = fetchItem.map(
      ({ gallery, id, name, prices, brand, description, attributes }) => {
        const obj = {
          product: gallery,
          id: id,
          name,
          brand,
          price: prices,
          description,
          attributes,
        };
        return obj;
      }
    );
    this.setState({ item: item });

    for (let data of item) {
      data.price.map(({ amount, currency }) => {
        if (currency.symbol.trim() === this.props.currentSymbol.trim()) {
          return this.setState({ price: amount });
        }
        return amount;
      });
    }
  }

  choiseItem = (e) => {
    const { src } = e.currentTarget.dataset;
    this.setState({ currentImage: src });
  };

  selectActive = (e) => {
    const { id } = e.target;
    const { index } = e.target.dataset;
    const { name } = e.target.dataset;
    this.setState({
      active: index,
      activeId: id,
      activeAtribute: name,
    });
  };
  render() {
    const { item, currentImage, active, activeAtribute, activeId } = this.state;
    const { currentSymbol } = this.props;
    return (
      <div className="container">
        <div className="container_photo">
          <ul className="item_list">
            {item &&
              item.map(({ id, product }) => {
                return product.map((src) => {
                  return (
                    <li key={v4()} data-src={src} onClick={this.choiseItem}>
                      <img height="80" src={src} alt="Item" />
                    </li>
                  );
                });
              })}
          </ul>
          {currentImage && (
            <div>
              <img height="511" src={currentImage} alt="Current" />
            </div>
          )}
        </div>
        <ul>
          {item &&
            item.map(({ name, brand, gallery, id, attributes, prices }) => {
              console.log(id);
              return (
                <li key={v4()}>
                  <div key={v4()}>
                    <div>
                      <p>{brand}</p>
                      <p>{name}</p>
                      <p>
                        {currentSymbol.trim()}
                        {prices.map((data) => {
                          if (
                            data.currency.symbol.trim() === currentSymbol.trim()
                          ) {
                            return data.amount;
                          }
                          return "";
                        })}
                      </p>
                    </div>

                    {attributes.map((dataAtr) => {
                      return (
                        <div key={v4()}>
                          <p>{dataAtr.id}:</p>
                          <div className="options_container" key={v4()}>
                            {dataAtr.items.map((dataItem, i) => {
                              if (dataAtr.id === "Color") {
                                return (
                                  <button
                                    className={
                                      Number(active) === i &&
                                      activeId === id &&
                                      activeAtribute === dataAtr.id
                                        ? "cart_options-color--active"
                                        : "cart_options-color"
                                    }
                                    key={v4()}
                                    onClick={this.selectActive}
                                  >
                                    <div
                                      data-index={i}
                                      data-name={dataAtr.id}
                                      id={id}
                                      style={{
                                        backgroundColor: dataItem.value,
                                        width: "32px",
                                        height: "32px",
                                      }}
                                    ></div>
                                  </button>
                                );
                              }
                              return (
                                <button
                                  className={
                                    Number(active) === i &&
                                    activeId === id &&
                                    activeAtribute === dataAtr.id
                                      ? "cart_change-options--active"
                                      : "cart_change-options"
                                  }
                                  key={v4()}
                                  onClick={this.selectActive}
                                  id={id}
                                  data-index={i}
                                  data-name={dataAtr.id}
                                >
                                  {dataItem.value}
                                </button>
                              );
                            })}
                          </div>
                        </div>
                      );
                    })}
                  </div>
                </li>
              );
            })}
        </ul>
      </div>
    );
  }
}
