import { Component } from "react";
import { v4 } from "uuid";
import Api from "./Api";
const fetchProduct = new Api();

export default class ItemPage extends Component {
  state = {
    item: null,
    price: 0,
    currentImage: null,
  };

  async componentDidMount() {
    const fetchItem = await fetchProduct.getProductId(this.props.itemId);
    const item = fetchItem.map(
      ({ gallery, id, name, prices, brand, description, attributes }) => {
        const obj = {
          product: gallery,
          id: id,
          name,
          brand,
          price: prices,
          description,
          attributes,
        };
        return obj;
      }
    );
    this.setState({ item: item });

    for (let data of item) {
      data.price.map(({ amount, currency }) => {
        if (currency.symbol.trim() === this.props.currentSymbol.trim()) {
          return this.setState({ price: amount });
        }
        return amount;
      });
    }
  }

  choiseItem = (e) => {
    const { src } = e.currentTarget.dataset;
    this.setState({ currentImage: src });
  };
  render() {
    const { item, price, currentImage } = this.state;
    console.log(item);
    return (
      <div className="container">
        <div className="container_photo">
          <ul className="item_list">
            {item &&
              item.map(({ id, product }) => {
                return product.map((src) => {
                  return (
                    <li key={v4()} data-src={src} onClick={this.choiseItem}>
                      <img height="80" src={src} alt="Item" />
                    </li>
                  );
                });
              })}
          </ul>
          {currentImage && (
            <div>
              <img height="511" src={currentImage} alt="Current" />
            </div>
          )}
        </div>
        <div></div>
      </div>
    );
  }
}
