  const { loading, error, data } = useQuery(CARD);

  if (loading) return <p>Loading...</p>;
  if (error) return <p>Error :(</p>;
  console.log(data.currencies);

  console.log(data.currencies.label);
  return (
    <div className="App">
      <header className="App-header">
        {/* {data &&
          data.map((data) => {
            console.log(data);
          })} */}
      </header>
    </div>
  );
}

export default App;
