import { Component } from "react";
import { v4 } from "uuid";
import Api from "./Api";
const fetchProduct = new Api();

export default class ItemPage extends Component {
  state = {
    item: null,
    price: 0,
    currentImage: null,
  };

  async componentDidMount() {
    const fetchItem = await fetchProduct.getProductId(this.props.itemId);
    const item = fetchItem.map(
      ({ gallery, id, name, prices, brand, description, attributes }) => {
        const obj = {
          product: gallery,
          id: id,
          name,
          brand,
          price: prices,
          description,
          attributes,
        };
        return obj;
      }
    );
    this.setState({ item: item });

    for (let data of item) {
      data.price.map(({ amount, currency }) => {
        if (currency.symbol.trim() === this.props.currentSymbol.trim()) {
          return this.setState({ price: amount });
        }
        return amount;
      });
    }
  }

  choiseItem = (e) => {
    const { src } = e.currentTarget.dataset;
    this.setState({ currentImage: src });
  };
  render() {
    const { item, price, currentImage } = this.state;
    const { currentSymbol } = this.props;
    return (
      <div className="container">
        <div className="container_photo">
          <ul className="item_list">
            {item &&
              item.map(({ id, product }) => {
                return product.map((src) => {
                  return (
                    <li key={v4()} data-src={src} onClick={this.choiseItem}>
                      <img height="80" src={src} alt="Item" />
                    </li>
                  );
                });
              })}
          </ul>
          {currentImage && (
            <div>
              <img height="511" src={currentImage} alt="Current" />
            </div>
          )}
        </div>
        <div>
          {item.map(({ name, brand, gallery, id, attributes, prices }) => {
            return (
              <li key={v4()}>
                <div key={v4()}>
                  <div>
                    <p>{brand}</p>
                    <p>{name}</p>
                    <p>
                      {currentSymbol.trim()}
                      {prices.map((data) => {
                        if (
                          data.currency.symbol.trim() === currentSymbol.trim()
                        ) {
                          return data.amount;
                        }
                        return "";
                      })}
                    </p>
                  </div>

                  {attributes.map((dataAtr) => {
                    return (
                      <div key={v4()}>
                        <p>{dataAtr.id}:</p>
                        <div className="options_container" key={v4()}>
                          {dataAtr.items.map((dataItem, i) => {
                            if (dataAtr.id === "Color") {
                              return (
                                <>
                                  {activePageCart ? (
                                    <button
                                      className={
                                        Number(active) === i &&
                                        activeId === id &&
                                        activeAtribute === dataAtr.id
                                          ? "cart_options-color--active"
                                          : "cart_options-color"
                                      }
                                      key={v4()}
                                      onClick={this.selectActive}
                                    >
                                      <div
                                        data-index={i}
                                        data-name={dataAtr.id}
                                        id={id}
                                        style={{
                                          backgroundColor: dataItem.value,
                                          width: "32px",
                                          height: "32px",
                                        }}
                                      ></div>
                                    </button>
                                  ) : (
                                    <button
                                      className={
                                        Number(active) === i &&
                                        activeId === id &&
                                        activeAtribute === dataAtr.id
                                          ? "options_color--active"
                                          : "options_color"
                                      }
                                      key={v4()}
                                      onClick={this.selectActive}
                                    >
                                      <div
                                        data-index={i}
                                        data-name={dataAtr.id}
                                        id={id}
                                        style={{
                                          backgroundColor: dataItem.value,
                                          width: "16px",
                                          height: "16px",
                                        }}
                                      ></div>
                                    </button>
                                  )}
                                </>
                              );
                            }
                            return (
                              <>
                                {activePageCart ? (
                                  <button
                                    className={
                                      Number(active) === i &&
                                      activeId === id &&
                                      activeAtribute === dataAtr.id
                                        ? "cart_change-options--active"
                                        : "cart_change-options"
                                    }
                                    key={v4()}
                                    onClick={this.selectActive}
                                    id={id}
                                    data-index={i}
                                    data-name={dataAtr.id}
                                  >
                                    {dataItem.value}
                                  </button>
                                ) : (
                                  <button
                                    className={
                                      Number(active) === i &&
                                      activeId === id &&
                                      activeAtribute === dataAtr.id
                                        ? "bag_change-options--active"
                                        : "bag_change-options"
                                    }
                                    key={v4()}
                                    onClick={this.selectActive}
                                    id={id}
                                    data-index={i}
                                    data-name={dataAtr.id}
                                  >
                                    {dataItem.value}
                                  </button>
                                )}
                              </>
                            );
                          })}
                        </div>
                      </div>
                    );
                  })}
                </div>
                {/* <div className="qwe"> */}
                <div
                  className={
                    activePageCart
                      ? "cart_container-counter"
                      : "bag_container-counter"
                  }
                  key={v4()}
                >
                  <button
                    id={id}
                    data-name="increment"
                    onClick={this.changeAmount}
                  ></button>

                  <span>
                    {localStorage.getItem("qwe")
                      ? arr.reduce((acc, val) => {
                          if (id === val) {
                            acc += 1;
                          }
                          return acc;
                        }, 1)
                      : 1}
                  </span>
                  <button
                    id={id}
                    data-name="decrement"
                    onClick={this.changeAmount}
                  ></button>
                </div>
                {activePageCart ? (
                  // <div className="cart_container-img" key={v4()}>
                  <img
                    src={gallery}
                    alt="Item in bag"
                    width="200px"
                    height="auto"
                  />
                ) : (
                  // </div>
                  // <div className="asd" key={v4()}>
                  <img
                    src={gallery}
                    alt="Item in bag"
                    width="121"
                    height="auto"
                  />
                  // </div>
                )}
                {/* </div> */}
              </li>
            );
          })}
        </div>
      </div>
    );
  }
}
