import { Component } from "react";
import Api from "./Api";
const fetchProduct = new Api();

export default class ItemPage extends Component {
  state = {
    item: null,
    price: 0,
  };

  async componentDidMount() {
    const fetchItem = await fetchProduct.getProductId(this.props.itemId);
    const item = fetchItem.map(({ gallery, id, name, prices, brand }) => {
      const obj = {
        product: gallery,
        id: id,
        name,
        brand,
        price: prices,
      };
      return obj;
    });
    this.setState({ item: item });

    for (let data of item) {
      data.price.map(({ amount, currency }) => {
        if (currency.symbol.trim() === this.props.currentSymbol.trim()) {
          return this.setState({ price: amount });
        }
        return amount;
      });
    }
  }
  render() {
    const { item, price } = this.state;
    return (
      <div>
        {item &&
          item[0].product.map((data) => {
            console.log(data);
            return (
              <div>
                <img src={data} alt="Item" width="79" height="80" />
              </div>
            );
          })}
      </div>
    );
  }
}
