import { Component } from "react";
import { NavLink } from "react-router-dom";
import Logo from "../images/logo-transparent.svg";
// import ArrowUp from "../images/arrow-up.svg";
import Basket from "../images/Empty-Cart.svg";
import ArrowDown from "../images/arrow-down.svg";

export default class Header extends Component {
  changeCurrency(e) {
    console.log(e);
  }

  render() {
    return (
      <header className="container-header header">
        <nav className="nav">
          <ul className="nav_list">
            <li className="nav_item">
              <NavLink
                className={({ isActive }) =>
                  `${isActive ? "active" : "nav_link"}`
                }
                to="/"
              >
                Women
              </NavLink>
            </li>
            <li className="nav_item">
              <NavLink
                className={({ isActive }) =>
                  `${isActive ? "active" : "nav_link"}`
                }
                to="*"
              >
                Men
              </NavLink>
            </li>
            <li className="nav_item">
              <NavLink
                className={({ isActive }) =>
                  `${isActive ? "active" : "nav_link"}`
                }
                to="*"
              >
                Kids
              </NavLink>
            </li>
          </ul>
        </nav>
        <div className="container_logo">
          <img src={Logo} alt="Logo" className="logo" />
        </div>
        <div className="container_basket">
          <span>$</span>
          <button onClick={this.changeCurrency} className="btn_arrow">
            <img className="arrow_down" src={ArrowDown} alt="Arrow up" />
          </button>
          <button className="btn_basket ">
            <img src={Basket} alt="Basket" />
          </button>
        </div>
      </header>
    );
  }
}
