import { Component } from "react";

// import { gql } from "@apollo/client";
// import { Query } from "@apollo/client/react/components";

// const ITEMS = gql`
//   query {
//     category {
//       products {
//         category
//       }
//     }
//   }
// `;

export default class Api extends Component {
  state = {
    qwe: [],
  };

  choiseCategory() {
    return ({ loading, data, error }) => {
      if (data) {
        const { products } = data.category;
        let unigue = [];
        products.map(({ category }) => {
          if (!unigue.includes(category)) {
            return unigue.push(category);
          }
        });
        const result = unigue.map((data) => {
          const qwe = data[0].toUpperCase() + data.slice(1);
          console.log(qwe);
          return (
            <li className="category_list-item" key={data}>
              {data}
            </li>
          );
        });
        return result;
      }
    };
  }
}
