import { Component } from "react";

export default class ModalBag extends Component {
  state = {
    itemsBag: null,
    symbol: this.props.symbol,
  };

  componentDidMount() {
    const itemStorage = JSON.parse(localStorage.getItem("productItems"));
    if (itemStorage) {
      const item = itemStorage.map(
        ({ name, brand, gallery, id, prices, attributes, description }) => {
          return {
            name,
            brand,
            gallery: gallery[0],
            id,
            attributes,
            description,
            prices,
          };
        }
      );
      return this.setState({ itemsBag: item });
    }
  }

  render() {
    const { itemsBag, symbol } = this.state;
    return (
      <div className="modal_container-bag">
        {itemsBag ? (
          <div>
            <p className="bag_title">
              <span>My bag,</span>
              {` ${itemsBag.length} items`}
            </p>
            <ul>
              {itemsBag.map(
                ({
                  name,
                  brand,
                  gallery,
                  id,
                  attributes,
                  description,
                  prices,
                }) => {
                  console.log(attributes);
                  return (
                    <li key={id}>
                      <p>{brand}</p>
                      <p>{name}</p>
                      <p>
                        {prices.map((data) => {
                          if (data.currency.symbol.trim() === symbol.trim()) {
                            return data.amount;
                          }
                          return "";
                        })}
                      </p>
                      {attributes.map((dataAtr) => {
                        return (
                          <div>
                            <p>{dataAtr.id}:</p>
                            {/* <div>
                              {dataAtr.items.map((dataItem) => {
                                return <p>{dataItem}</p>;
                              })}
                            </div> */}
                          </div>
                        );
                      })}
                    </li>
                  );
                }
              )}
            </ul>
          </div>
        ) : (
          <div>Корзина пуста</div>
        )}
      </div>
    );
  }
}

