import { Component } from "react";
import { v4 } from "uuid";
import { PRODUCT } from "./Api";
// import Basket from "../images/Empty-Cart.svg";
import { client } from "../index";
import Notiflix from "notiflix";

export default class HomePage extends Component {
  state = {
    showBasker: false,
    productAll: [],
  };

  async componentDidMount() {
    try {
      const result = await client.query({
        query: PRODUCT,
      });
      // this.setState({ product: result.data.category.products });
      result.data.category.products.map((data) => {
        return this.setState((prevState) => {
          return prevState.productAll.push(data.gallery[0]);
        });
      });
    } catch (error) {
      Notiflix.Notify.failure(`${error.message}`);
    }
  }

  // basket = (e) => {
  //   console.log(e.currentTarget);
  //   this.setState({ showBasket: !this.state.showBasket });
  // };

  render() {
    const { productAll } = this.state;
    return (
      <main className="container">
        <p className="category-name">Category name</p>
        <ul className="gallery_list">
          {productAll.length > 0 &&
            productAll.map((data) => {
              console.log(productAll);
              return (
                <li className="gallery_item" key={v4()}>
                  <img width="354" height="330" src={data} alt="our products" />
                </li>
              );
            })}
        </ul>
      </main>
    );
  }
}
