import { Component } from "react";
// import { v4 } from "uuid";
import { PRODUCT } from "./Api";
import Basket from "../images/Basket_card.svg";
import { client } from "../index";
import Notiflix from "notiflix";

export default class HomePage extends Component {
  state = {
    showBasker: false,
    productAll: [],
    id: "",
    priceHomePage: [],
    activeS: "",
  };

  async componentDidMount() {
    const activeCur = localStorage.getItem("currencySymbol");
    this.setState({ activeS: activeCur });
    try {
      const result = await client.query({
        query: PRODUCT,
      });
      const qwe = result.data.category.products.map(
        ({ gallery, id, name, prices, brand }) => {
          const obj = {
            product: gallery[0],
            id: id,
            name,
            brand,
            price: prices,
          };
          return obj;
        }
      );
      // console.log(this.state.productAll);
      this.setState({ productAll: qwe });

      for (let data of qwe) {
        data.price.map(({ amount, currency }) => {
          // console.log("object");
          return this.setState((prevState) => {
            const arr = prevState.priceHomePage.push(amount);
            console.log(arr);
            // return {
            //   priceHomePage: prevState.priceHomePage.push(amount),
            // };
          });
        });
      }
    } catch (error) {
      console.log(error.message);
      Notiflix.Notify.failure(`${error.message}`);
    }
  }

  componentDidUpdate(prev, state) {
    if (state.activeS !== this.props.symbolCard) {
      console.log(state.productAll);
      state.activeS = this.props.symbolCard;
      // if (state.priceHomePage.length > 0) {
      //   state.priceHomePage = [];
      // }
      for (let data of state.productAll) {
        // console.log(data.price);
        data.price.map(({ amount, currency }) => {
          if (currency.symbol.trim() === this.props.symbolCard.trim()) {
            return state.priceHomePage.push(amount);
          }
          return amount;
        });
      }
      return;
    }
  }

  id = (e) => {
    const { id } = e.currentTarget;
    switch (e._reactName) {
      case "onMouseOver":
        this.setState({ id: id });
        break;
      case "onMouseLeave":
        this.setState({ id: "" });
        break;

      default:
        break;
    }
  };

  render() {
    const { productAll, priceHomePage } = this.state;
    // console.log(productAll);
    const { symbolCard } = this.props;
    return (
      <main className="container">
        <p className="category-name">Category name</p>
        <ul className="gallery_list">
          {productAll.length > 0 &&
            productAll.map(({ product, id, name, price, brand }, inx) => {
              return (
                <li
                  onMouseOver={this.id}
                  onMouseLeave={this.id}
                  id={id}
                  className="gallery_item"
                  key={id}
                >
                  <img
                    width="354"
                    height="330"
                    src={product}
                    alt="our products"
                  />
                  <p className="gallery_brand">
                    {name} {brand}
                  </p>
                  <p className="gallery_price">
                    {symbolCard}
                    {priceHomePage.length > 0 &&
                      price.map((data) => {
                        return priceHomePage.find((val, ind) => {
                          if (
                            inx === ind &&
                            val === data.amount &&
                            data.currency.symbol.trim() === symbolCard.trim()
                          ) {
                            // console.log(val);
                            return val;
                          }
                          return null;
                        });
                      })}
                  </p>
                  {id === this.state.id && (
                    <button className="btn_add-basket">
                      <img src={Basket} alt="Add to basket" />
                    </button>
                  )}
                </li>
              );
            })}
        </ul>
      </main>
    );
  }
}
