import { Component } from "react";
import { gql } from "@apollo/client";
import { Query } from "@apollo/client/react/components";

export default class ChoiceCurrency extends Component {
  choise = (e) => {
    const { textContent } = e.target;
    const activeCurrency = textContent.split("", 2).join("");
    this.props.relevantCurrency(activeCurrency);
    return activeCurrency;
  };

  render() {
    return (
      <ul className="currency_list">
        <Query query={CURRENCIES}>
          {({ loading, data, error }) => {
            // НЕ ЗАБЫТЬ ОБРАБОТАТЬ ОШИБКУ И ЗАГРУЗКУ!!!
            if (loading) return <div>Loading. . .</div>;
            if (data) {
              const currenciesItems = data.currencies.map(
                ({ symbol, label }) => {
                  return (
                    <li
                      onClick={this.choise}
                      key={symbol}
                      className="currency_item"
                    >
                      {symbol} {label}
                    </li>
                  );
                }
              );
              return currenciesItems;
            }
          }}
        </Query>
      </ul>
    );
  }
}
