import { Component } from "react";
import { useQuery, gql } from "@apollo/client";

const CURRENCIES = gql`
  query {
    currencies {
      label
      symbol
    }
  }
`;

export default class ChoiceCurrency extends Component {
  componentDidMount(prev, state) {
    const { loading, error, data } = useQuery(CURRENCIES);
    if (loading) return <p>Loading...</p>;
    if (error) return <p>Error :(</p>;
    console.log(data.currencies);
    console.log(data.currencies.label);
  }

  render() {
    return (
      <ul className="currency_list">
        <li className="currency_item">$ USD</li>
        <li className="currency_item">EUR</li>
        <li className="currency_item">UK</li>
      </ul>
    );
  }
}
