import { gql } from "@apollo/client";
import { Component } from "react";

export default class Api extends Component {
  getAllProduct = async () => {
    const result = await client.query({
      query: PRODUCT,
    });
  };
}

const PRODUCT = gql`
  query {
    category {
      products {
        id
        gallery
        brand
        inStock
        name
        prices {
          amount
          currency {
            symbol
          }
        }
      }
    }
  }
`;

const CURRENCIES = gql`
  query {
    currencies {
      label
      symbol
    }
  }
`;

export { PRODUCT, CURRENCIES };
