import { Component } from "react";
import { gql } from "@apollo/client";
import { Query } from "@apollo/client/react/components";
// import { Query } from "react-apollo";

const CURRENCIES = gql`
  query {
    currencies {
      label
      symbol
    }
  }
`;

export default class ChoiceCurrency extends Component {
  componentDidMount(prev, state) {
    // const { loading, error, data } = useQuery(CURRENCIES);
    // if (loading) return <p>Loading...</p>;
    // if (error) return <p>Error :(</p>;
    // console.log(data.currencies);
    // console.log(data.currencies.label);
  }

  render() {
    return (
      <ul className="currency_list">
        <Query query={CURRENCIES}>
          {({ loading, data }) => {
            if (loading) return <div>Loading. . .</div>;
            if (data) {
              const currenciesItems = data.currencies.map(
                ({ symbol, label }) => {
                  return (
                    <li key={symbol} className="currency_item">
                      {symbol} {label}
                    </li>
                  );
                }
              );
              return currenciesItems;
            }
          }}
        </Query>
      </ul>
    );
  }
}
