  componentDidMount() {
    const itemStorage = JSON.parse(localStorage.getItem("productItems"));
    if (itemStorage) {
      const qwe = itemStorage.map(
        ({ name, brand, gallery, id, attributes, description }) => {
          return {
            name: name,
            // brand,
            // gallery: gallery[0],
            // id,
            // attributes,
            // description,
          };
        }
      );
      console.log(qwe);
      return this.setState({ items: itemStorage });
    }
  }

  render() {
    const { items } = this.state;
    return (
      <div className="modal_container-bag">
        {items ? (
          <div>
            <p>
              <span>My bag,</span>
              {` ${items.length} items`}
            </p>
            <ul>
              {items.map((data) => {
                console.log(data);
              })}
            </ul>
          </div>
        ) : (
          <div>Корзина пуста</div>
        )}
      </div>
    );
  }
}
