import { Component } from "react";
import { gql } from "@apollo/client";
import { Query } from "@apollo/client/react/components";

const ITEMS = gql`
  query {
    category {
      products {
        category
      }
    }
  }
`;

export default class HomePage extends Component {
  render() {
    return (
      <main>
        <h1>Category name</h1>
        <ul>
          <Query query={ITEMS}>
            {({ loading, data, error }) => {
              if (data) {
                console.log(data);
                const { products } = data.category;
                console.log(products);
                const qwe = products[0].gallery.map((item) => {
                  return (
                    <li key={item}>
                      <img src={item} alt="item clothes" />
                    </li>
                  );
                });
                return qwe;
              }
            }}
          </Query>
        </ul>
      </main>
    );
  }
}
