import { Component } from "react";

export default class ModalBag extends Component {
  state = {
    itemsBag: null,
  };

  componentDidMount() {
    const itemStorage = JSON.parse(localStorage.getItem("productItems"));
    if (itemStorage) {
      const item = [];
      itemStorage.map(
        ({ name, brand, gallery, id, category, attributes, description }) => {
          attributes.map((data) => {
            return {
              name: name,
              brand,
              gallery: gallery[0],
              id,
              data: data.id,

              description,
            };
          });
        }
      );
      return this.setState({ itemsBag: item });
    }
  }

  render() {
    const { itemsBag } = this.state;
    return (
      <div className="modal_container-bag">
        {itemsBag ? (
          <div>
            <p>
              <span>My bag,</span>
              {` ${itemsBag.length} items`}
            </p>
            <ul>
              {itemsBag.map((data) => {
                // console.log(data);
              })}
            </ul>
          </div>
        ) : (
          <div>Корзина пуста</div>
        )}
      </div>
    );
  }
}
