import { Component } from "react";

export default class ModalBag extends Component {
  state = {
    itemsBag: null,
    symbol: this.props.symbol,
  };

  componentDidMount() {
    const itemStorage = JSON.parse(localStorage.getItem("productItems"));
    if (itemStorage) {
      const item = itemStorage.map(
        ({ name, brand, gallery, id, attributes, description }) => {
          return attributes.map((data) => {
            return {
              name,
              brand,
              gallery: gallery[0],
              id,
              view: data.id,
              label: data.items,
              description,
            };
          });
        }
      );
      return this.setState({ itemsBag: item });
    }
  }

  render() {
    const { itemsBag, symbol } = this.state;
    console.log(symbol);
    return (
      <div className="modal_container-bag">
        {itemsBag ? (
          <div>
            <p className="bag_title">
              <span>My bag,</span>
              {` ${itemsBag.length} items`}
            </p>
            <ul>
              {itemsBag.map(
                ({
                  name,
                  brand,
                  gallery,
                  id,
                  view,
                  label,
                  description,
                  symbol,
                }) => {
                  console.log(id);
                  return (
                    <li key={id}>
                      <p>{brand}</p>
                      <p>{name}</p>
                    </li>
                  );
                }
              )}
            </ul>
          </div>
        ) : (
          <div>Корзина пуста</div>
        )}
      </div>
    );
  }
}
