import { Component } from "react";
import { v4 } from "uuid";

export default class ModalBag extends Component {
  state = {
    itemsBag: null,
    symbol: this.props.symbol,
    total: 0,
    counter: 1,
  };

  componentDidMount() {
    const { symbol } = this.state;

    const itemStorage = JSON.parse(localStorage.getItem("productItems"));
    if (itemStorage) {
      const item = itemStorage.map(
        ({ name, brand, gallery, id, prices, attributes }) => {
          return {
            name,
            brand,
            gallery: gallery[0],
            id,
            attributes,
            prices,
          };
        }
      );
      const prices = item.flatMap((data) => {
        return data.prices;
      });

      const price = prices.reduce((acc, val) => {
        if (val.currency.symbol.trim() === symbol.trim()) {
          return (acc += val.amount);
        }
        return acc;
      }, 0);
      this.setState({ total: price });
      return this.setState({ itemsBag: item });
    }
  }

  changeAmount = (e) => {
    const { textContent } = e.target;
    const { counter } = this.state;
    console.log(e.target.id);

    switch (textContent) {
      case "+":
        this.setState((prevState) => ({
          counter: prevState.counter + 1,
        }));
        break;
      case "-":
        if (counter < 2) {
          return;
        }
        this.setState((prevState) => ({
          counter: prevState.counter - 1,
        }));
        break;

      default:
        return counter;
    }
  };

  render() {
    const { itemsBag, symbol, total, counter } = this.state;
    return (
      <div className="modal_container-bag">
        {itemsBag ? (
          <div>
            <p className="bag_title">
              <span>My bag,</span>
              {` ${itemsBag.length} items`}
            </p>
            <ul>
              {itemsBag.map(
                ({ name, brand, gallery, id, attributes, prices }) => {
                  return (
                    <li key={id} className="bag_item">
                      <div className="bag_container-info">
                        <div className="bag_denotation">
                          <p>{brand}</p>
                          <p>{name}</p>
                          <p>
                            {symbol}
                            {prices.map((data) => {
                              if (
                                data.currency.symbol.trim() === symbol.trim()
                              ) {
                                return data.amount;
                              }
                              return "";
                            })}
                          </p>
                        </div>

                        {attributes.map((dataAtr) => {
                          return (
                            <div key={v4()}>
                              <p className="options">{dataAtr.id}:</p>
                              <div className="options_container">
                                {dataAtr.items.map((dataItem) => {
                                  if (dataAtr.id === "Color") {
                                    return (
                                      <button
                                        className="options_color"
                                        key={v4()}
                                      >
                                        <div
                                          style={{
                                            backgroundColor: dataItem.value,
                                            width: "16px",
                                            height: "16px",
                                          }}
                                        ></div>
                                      </button>
                                    );
                                  }
                                  return (
                                    <span
                                      className="bag_change-options"
                                      key={v4()}
                                    >
                                      {dataItem.value}
                                    </span>
                                  );
                                })}
                              </div>
                            </div>
                          );
                        })}
                      </div>

                      <div className="bag_container-btn">
                        <button id={id} onClick={this.changeAmount}>
                          +
                        </button>
                        <span>{counter}</span>
                        <button id={id} onClick={this.changeAmount}>
                          -
                        </button>
                      </div>
                      <div>
                        <img
                          src={gallery}
                          alt="Item in bag"
                          width="121px"
                          height="190px"
                        />
                      </div>
                    </li>
                  );
                }
              )}
            </ul>
            <p>Total: {total}</p>
            <div>
              <button>View bag</button>
              <button>CHECK OUT</button>
            </div>
          </div>
        ) : (
          <div>Корзина пуста</div>
        )}
      </div>
    );
  }
}
