import { Component } from "react";
import { gql } from "@apollo/client";
import { Query } from "@apollo/client/react/components";

const ITEMS = gql`
  query {
    category {
      products {
        category
      }
    }
  }
`;

export default class HomePage extends Component {
  render() {
    return (
      <main>
        <h1>Category name</h1>
        <ul>
          <Query query={ITEMS}>
            {({ loading, data, error }) => {
              if (data) {
                const { products } = data.category;
                let unigue = [];
                products.map(({ category }) => {
                  if (!unigue.includes(category)) {
                    return unigue.push(category);
                  }
                });
                unigue.map((data) => {
                  console.log(data);
                  return <li key={data}>{data}</li>;
                });

                // console.log(products);
                // const qwe = products.map(({ category }) => {
                //
                // });
                // return qwe;
              }
            }}
          </Query>
        </ul>
      </main>
    );
  }
}
