import { Component } from "react";
import Api from "./Api";
import { gql } from "@apollo/client";
import { Query } from "@apollo/client/react/components";

const ITEMS = gql`
  query {
    category {
      products {
        category
      }
    }
  }
`;

export default class HomePage extends Component {
  qwe = () => {
    <Query query={ITEMS}>
      {({ loading, data, error }) => {
        if (data) {
          const { products } = data.category;
          let unigue = [];
          products.map(({ category }) => {
            if (!unigue.includes(category)) {
              return unigue.push(category);
            }
          });
          const result = unigue.map((data) => {
            const dataUpper = data[0].toUpperCase() + data.slice(1);
            return (
              <li className="category_list-item" key={dataUpper}>
                {dataUpper}
              </li>
            );
          });
          return result;
        }
      }}
      ;
    </Query>;
  };

  render() {
    return (
      <main className="container">
        <ul className="category_list">lknln</ul>
      </main>
    );
  }
}
